insert into products (id, productsName, brandID, productsDescription, categoryID, price, discount, lastUpdated, amountSold, statusID) values (267, 'desk', 45, 'Bàn đầu giường Cabo được thiết kế hình hộp chữ nhật với chất liệu được làm từ MDF màu walnut, chân bàn bằng kim loại sơn đen. Bàn đầu giường gồm 1 hộc kéo và 1 ngăn đựng. Hộc kéo có màu xanh olive cùng với với hoa văn độc đáo. Bàn đầu giường là sản phẩm lý tưởng trong thiết kế không gian phòng ngủ vì có thiết kế sang trọng, hiện đại.', 65, 8732681, 473635, '2023-09-28', 93, 5);
insert into products (id, productsName, brandID, productsDescription, categoryID, price, discount, lastUpdated, amountSold, statusID) values (268, 'sofa', 37, 'Bàn nước Maxine ghi dấu ấn với nét tròn tinh tế và bề mặt vân gỗ hài hòa. Kim loại mạ đồng được sử dụng mạnh mẽ cho kiểu chân lạ mắt, sang trọng, và được sử dùng làm bo viền cho toàn bộ sản phẩm vừa tạo điểm nhấn cho phòng khách và bảo vệ phần gỗ tránh bị va đập. Maxine – Nét nâu trâm sang trọng Maxine, mang thiết kế vượt thời gian, gửi gắm và gói gọn lại những nét đẹp của thiên nhiên và con người nhưng vẫn đầy tính ứng dụng cao trong suốt hành trình phiêu lưu của nhà thiết kế người Pháp Dominique Moal. Bộ sưu tập nổi bật với màu sắc nâu trầm sang trọng, là sự kết hợp giữa gỗ, da bò và kim loại vàng bóng. Đặc biệt trên mỗi sản phẩm, những nét bo viên, chi tiết kết nối được sử dụng kéo léo tạo ra điểm nhất rất riêng cho bộ sưu tập. Maxine thẻ hiện nét trầm tư, thư giãn để tận hưởng không gian sống trong nhịp sống hiện đại. Sản phẩm thuộc BST Maxine được sản xuất tại Việt Nam. Coastal gây ấn tượng bằng những đường cong bồng bềnh, theo xu hướng Modern Organic – gần gũi với thiên nhiên mà vẫn hiện đại, thoải mái. Điểm đặc biệt của BST lần này nằm ở sự tỉ mỉ của những người thợ thủ công lành nghề, họ đã hoàn thành những đường may uốn lượn không tì vết, mang đến một thiết kế chỉn chu, "cân" mọi góc nhìn. Cảm giác êm ái và thư thái sẽ là những tính từ đầu tiên được nhắc đến khi trải nghiệm sofa Coastal.', 32, 4685469, 762083, '2023-08-11', 808, 5);
