insert into products (id, productsName, brandID, productsDescription, categoryID, price, discount, lastUpdated, amountSold, statusID) values (540, 'sofa', 5, 'Bàn nước Maxine ghi dấu ấn với nét tròn tinh tế và bề mặt vân gỗ hài hòa. Kim loại mạ đồng được sử dụng mạnh mẽ cho kiểu chân lạ mắt, sang trọng, và được sử dùng làm bo viền cho toàn bộ sản phẩm vừa tạo điểm nhấn cho phòng khách và bảo vệ phần gỗ tránh bị va đập. Maxine – Nét nâu trâm sang trọng Maxine, mang thiết kế vượt thời gian, gửi gắm và gói gọn lại những nét đẹp của thiên nhiên và con người nhưng vẫn đầy tính ứng dụng cao trong suốt hành trình phiêu lưu của nhà thiết kế người Pháp Dominique Moal. Bộ sưu tập nổi bật với màu sắc nâu trầm sang trọng, là sự kết hợp giữa gỗ, da bò và kim loại vàng bóng. Đặc biệt trên mỗi sản phẩm, những nét bo viên, chi tiết kết nối được sử dụng kéo léo tạo ra điểm nhất rất riêng cho bộ sưu tập. Maxine thẻ hiện nét trầm tư, thư giãn để tận hưởng không gian sống trong nhịp sống hiện đại. Sản phẩm thuộc BST Maxine được sản xuất tại Việt Nam. Miami khoác lên mình một màu xám tinh tế, tối giản, mang đến không gian phòng khách hiện đại, trang nhã. Bàn nước Maxine ghi dấu ấn với nét tròn tinh tế và bề mặt vân gỗ hài hòa. Kim loại mạ đồng được sử dụng mạnh mẽ cho kiểu chân lạ mắt, sang trọng, và được sử dùng làm bo viền cho toàn bộ sản phẩm vừa tạo điểm nhấn cho phòng khách và bảo vệ phần gỗ tránh bị va đập. Maxine – Nét nâu trâm sang trọng Maxine, mang thiết kế vượt thời gian, gửi gắm và gói gọn lại những nét đẹp của thiên nhiên và con người nhưng vẫn đầy tính ứng dụng cao trong suốt hành trình phiêu lưu của nhà thiết kế người Pháp Dominique Moal. Bộ sưu tập nổi bật với màu sắc nâu trầm sang trọng, là sự kết hợp giữa gỗ, da bò và kim loại vàng bóng. Đặc biệt trên mỗi sản phẩm, những nét bo viên, chi tiết kết nối được sử dụng kéo léo tạo ra điểm nhất rất riêng cho bộ sưu tập. Maxine thẻ hiện nét trầm tư, thư giãn để tận hưởng không gian sống trong nhịp sống hiện đại. Sản phẩm thuộc